var expect = require('chai').expect;


describe('random-email: ', function () {

  var Email = require('../../');

  it('Email()', function () {
    expect(Email()).to.be.a('string');
    expect(Email()).to.be.match(/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(.[a-zA-Z0-9_-])+/);
  });

  it('Email({ domain: "random.com" })', function () {
    expect(Email({ domain: 'random.com' })).to.be.match(/random\.com$/);
  });
});